
const tSidebar=document.querySelector('.sidebar-toggle');tSidebar.addEventListener('click',function(){const acSidebar=document.querySelector('.sidebar');const c=this.childNodes;c[1].classList.toggle('tw-rotate-180');acSidebar.classList.toggle('small');});const navCollapse=document.querySelectorAll('.sidebar-nav-collapse');for(var i=0;i<navCollapse.length;i++){navCollapse[i].addEventListener('click',function(e){const acSidebar=document.querySelector('.sidebar');const hasClass=acSidebar.classList.contains('small');const collapse=this.nextElementSibling;const c=this.childNodes;if(hasClass==false){collapse.classList.toggle('open-sub');}
else{e.preventDefault();}});}
const iSearch=document.querySelector('.sidebar-search-icon');iSearch.addEventListener('click',function(){const acSidebar=document.querySelector('.sidebar');const hasClass=acSidebar.classList.contains('small');if(hasClass==true){acSidebar.classList.toggle('small');document.querySelector('.sidebar-search-input').focus();}
else{document.querySelector('.sidebar-search-input').focus();}});const toggleNav=document.querySelector('.navigation-toggle');toggleNav.addEventListener("click",function(){const navMobile=document.querySelector('#sidebar');const blur=document.querySelector('#blur');navMobile.classList.toggle('mobile');blur.classList.toggle('tw-blur');});